import UIKit
import RxSwift
import RxCocoa
import BaseCore

class LoginViewController: EducaViewController<LoginViewModel> {
    
    @IBOutlet weak var retryButton: UIButton!
    @IBOutlet weak var testButton: UIButton!
    
    private let bag = DisposeBag()
    
    override func viewDidLoad() {
        super.viewDidLoad()
        setupUI()
    }
    
    deinit {
//        Common.log("LoginViewController deinit âœ…")
    }
    
    private func setupUI() {
        
    }
    
    override func bindViewModel() {
        let input = LoginViewModel.Input(
            retryTrigger: retryButton.rx.tap.asDriver(), testCallTrigger: testButton.rx.tap.asDriver())
        let output = viewModel.transform(input: input)
        
        output.testOutput
            .drive { value in
                print("value is \(value)")
            }
            .disposed(by: bag)
        
        output.test
            .drive()
            .disposed(by: bag)
        
    }
}
